/*!50003 CREATE*/ /*!50020 DEFINER=`forbac_site`@`%`*/ /*!50003 FUNCTION `compareNumSevers`(_actionID integer, _orderID integer) RETURNS tinyint(3)
    DETERMINISTIC
BEGIN

DECLARE planCountSer integer;

DECLARE responseCountSer integer;

SET planCountSer = (Select COUNT(id) From order_server_ids Where orderID=_orderID);
SET responseCountSer = (Select COUNT(id) From order_server_action_ids Where actionID = _actionID and orderID=_orderID);

 IF (planCountSer!=0 && planCountSer = responseCountSer) THEN
 	RETURN 1;
 ELSE 
 	RETURN 0;
 END IF;

END */;;
