/*!50003 CREATE*/ /*!50020 DEFINER=`forbac_site`@`%`*/ /*!50003 FUNCTION `nextNumOrder`() RETURNS int(11)
BEGIN

 DECLARE _num integer;
 
    SET _num = (SELECT max(num_order) FROM orders) ;
    IF _num IS NULL THEN
        SET _num=1;
    ELSE
        SET _num=_num+1;
    END IF;


return _num;
END */;;
