/*!50003 CREATE*/ /*!50020 DEFINER=`forbac_site`@`%`*/ /*!50003 PROCEDURE `insertUpdateOrderMultiServersIds`(_id integer, _multi bool, _multidouble bool)
    DETERMINISTIC
BEGIN

START TRANSACTION;
 

 IF (_id !='') THEN
  Delete From order_server_ids Where orderID = _id;
  
  IF (_multi = true ) THEN
	Insert Into order_server_ids (orderID,serverID)
	SELECT _id,s.id FROM servers s 
	Where s.part_multi="1" and s.iddouble='' and s.idmultidouble='';
  END IF;
  IF (_multidouble = true ) THEN
	Insert Into order_server_ids (orderID,serverID)
	SELECT _id,s.id FROM servers s 
	Where s.part_multi="1" and s.idmultidouble!='';
  END IF;
END IF;
COMMIT;
END */;;
